import { useState, useEffect, useRef } from "react";
import * as React from "react";
import * as Animatable from 'react-native-animatable'
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  Image,
  Dimensions,
  TouchableOpacity,
  TouchableWithoutFeedback,
  Keyboard,
} from 'react-native';
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";
import Logo from '../../../assets/logoIcon/logo.png';
import { Icon } from 'react-native-elements';

const BGColor = "#4D4A95"

export default function registrar() {

    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');

    const registerFB = () => {

        const auth = getAuth();
        createUserWithEmailAndPassword(auth, email, password)
          .then((userCredential) => {
            // Signed in
            const user = userCredential.user;
            // ...
          })
          .catch((error) => {
            const errorCode = error.code;
            const errorMessage = error.message;
            // ..
          });
      
      }

  return (
    <TouchableWithoutFeedback onPress={() => Keyboard.dismiss()}>
      <View style={styles.container}>
        <View style={styles.centerizedView}>
          <View style={styles.authBox}>
            <View style={styles.logoBox}>
            <Animatable.Image 
          source={Logo} 
          style={{
            height: 125, 
            width: 125,
          }}
          animation='rubberBand'
          duration={2500}
          delay={2500}
          useNativeDriver
          iterationCount={Infinity}
            />
            </View>
            <Text style={styles.loginTitleText}>Cadastrar</Text>
            <View style={styles.inputBox}>
            <Icon
              style={styles.inputIcon}
              name='person'
              type='ionicons'
              color='#5352ed'
            />
              <TextInput
                style={styles.input}
                placeholder='Email'
                autoCapitalize='none'
                keyboardType='email-address'
                textContentType='emailAddress'
                onChangeText={setEmail}
              />
            </View>
            <View style={styles.inputBox}>
            <Icon
              style={styles.inputIcon}
              name='lock'
              type='ionicons'
              color='#5352ed'
            />
              <TextInput
                placeholder='Senha'
                style={styles.input}
                autoCapitalize='none'
                secureTextEntry={true}
                textContentType='password'
                onChangeText={setPassword}
              />
            </View>
            <TouchableOpacity style={styles.loginButton} onPress={registerFB}>
              <Text style={styles.loginButtonText}>Cadastrar</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </TouchableWithoutFeedback>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    position: 'relative',
    backgroundColor: BGColor,
    justifyContent: 'center'
  },
  centerizedView: {
    width: '100%',
  },
  authBox: {
    width: '80%',
    backgroundColor: '#fafafa',
    borderRadius: 20,
    alignSelf: 'center',
    paddingHorizontal: 14,
    paddingBottom: 30,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  logoBox: {
    backgroundColor: BGColor,
    borderRadius: 1000,
    alignSelf: 'center',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    top: -50,
    marginBottom: -50,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 1,
    },
    shadowOpacity: 0.2,
    shadowRadius: 1.41,
    elevation: 20,
  },
  loginTitleText: {
    fontSize: 26,
    fontWeight: 'bold',
    marginTop: '5%',
    marginBottom: '3%'
  },
  inputBox: {
    height: 40,
    borderRadius: 10,
    backgroundColor: '#f1f3f6',
    marginTop: 10,
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
  },
  inputLabel: {
    fontSize: 18,
    marginBottom: 6,
  },
  input: {
    height: 40,
    flex: 1,
    fontFamily: 'SSPRegular',
    fontSize: 16,
    color: '#333',
  },
  loginButton: {
    backgroundColor: BGColor,
    marginTop: '15%',
    marginBottom: '5%',
    paddingVertical: 10,
    borderRadius: 4,
  },
  loginButtonText: {
    color: '#fff',
    textAlign: 'center',
    fontSize: 20,
    fontWeight: 'bold',
  },
  registerText: {
    textAlign: 'center',
    marginTop: 20,
    fontSize: 16,
  },
  forgotPasswordText: {
    textAlign: 'center',
    marginTop: 12,
    fontSize: 16,
  },
  inputIcon: {
    paddingHorizontal: 8,
  },
});
