import React, {useRef} from 'react'
import { Animated, ScrollView, StyleSheet, Text, View, SafeAreaView, Image, useWindowDimensions, TouchableHig} from 'react-native';
import {widthPercentageToDP as wp, heightPercentageToDP as hp} from 'react-native-responsive-screen';

const images = [
    {id: 1, img: require("../../../assets/GameImg/NoImage.png"), title: "Treinamento", color: "black"},
    {id: 2, img: require("../../../assets/GameImg/NoImage.png"), title: "Acerto de Contas", color: "black"},
    {id: 3, img: require("../../../assets/GameImg/NoImage.png"), title: "Travar Batalha", color: "black"},
    {id: 4, img: require("../../../assets/GameImg/NoImage.png"), title: "Guerra de Clãs", color: "black"}
]

const BGColor = "#4D4A95";

export default function TelaInicio() {

    const scrollX = useRef(new Animated.Value(0)).current;

    let {width: windowWidth, height: windowHeight} = useWindowDimensions();
    windowHeight = windowHeight - 500;

    return (
        <SafeAreaView style={styles.container}>
            <View>
                <ScrollView
                horizontal={true}
                style={styles.SViewStyle}
                paddingEnabled
                showsHorizontalScrollIndicator={false}
                scrollEventThrottle={16}
                pagingEnabled={true}
                onScroll={Animated.event(
                    [{nativeEvent: {contentOffset: {x: scrollX}}}],
                    {useNativeDriver: false}
                )}
                >
                    {images.map((image, imageIndex) => {
                        return (
                            <Animated.View style={{width: windowWidth}} key={imageIndex}>
                                <TouchableOpacity style={styles.btnOpt}>
                                    <Image 
                                    source={image.img}
                                    aspectRatio={0.81298828125}
                                    style={{
                                        width: wp('75'),
                                        height: undefined,
                                        borderRadius: 35,
                                    }}/>
                                    <Text style={{position: 'absolute'}}>{image.title}</Text>
                                </TouchableOpacity>
                            </Animated.View>
                        )
                    })}
                </ScrollView>
            </View>
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: BGColor,
    },
    SViewStyle:{
    },
    btnOpt:{
        flex: 1,
        marginVertical: '40%',
        overflow: 'hidden',
        alignSelf: 'center',
        alignItems: 'center', 
        justifyContent: 'flex-start'
    }
})
