import React, { useState, useEffect, useRef } from "react";
import { Animated, Image, StatusBar, StyleSheet, Text, View, Keyboard, Dimensions, TouchableWithoutFeedback, TouchableOpacity, TextInput } from 'react-native';
import * as animatable from 'react-native-animatable'
import { useSafeAreaInsets } from "react-native-safe-area-context";
import Logo from '../../assets/logoIcon/logo.png';
import { useFonts } from 'expo-font';
import SourceSansProLight from '../../assets/fonts/SourceSansPro/SourceSansPro-Light.ttf';
import SourceSansProRegular from '../../assets/fonts/SourceSansPro/SourceSansPro-Regular.ttf';
import SourceSansProBold from '../../assets/fonts/SourceSansPro/SourceSansPro-Bold.ttf';
import { Icon } from 'react-native-elements';

const BGColor = "#4D4A95"

export default function InitialLogo() {

const edges = useSafeAreaInsets();
const fadeScreen = useRef(new Animated.Value(1)).current;
const fadeLogo = useRef(new Animated.Value(0)).current;
const fadeText = useRef(new Animated.Value(0)).current;

const [loaded] = useFonts({
  SourceSansProLight,
  SourceSansProRegular,
  SourceSansProBold,
});

if (!loaded || !Logo) {
  return <Text>Loading...</Text>;
}

useEffect(() => {

  setTimeout(() => {
    
    Animated.sequence([
      Animated.timing(fadeLogo,
        {
          toValue: 1,
          useNativeDriver: true,
          duration: 1000
        }
      ),
      Animated.timing(fadeText,
        {
          toValue: 1,
          useNativeDriver: true,
          duration: 1000
        }
      ),
      Animated.timing(fadeText,
        {
          toValue: 0,
          delay: 1000,
          useNativeDriver: true,
          duration: 500
        }
        )
    ]).start()

  }, 350)

},[]);

    return(
    <Animated.View style={{
      position: 'absolute',
      backgroundColor: BGColor,
      top: 0,
      bottom: 0,
      right: 0,
      left: 0,
      opacity: fadeScreen
    }}>
      <StatusBar backgroundColor={BGColor}/>
        <Animated.View
        style={{
          alignItems: 'center',
          justifyContent: 'center',
          flex: 1,
          opacity: fadeLogo
        }}
        >
          <animatable.Image 
          source={Logo} 
          style={{
            height: 150, 
            width: 150
          }}
          animation='rubberBand'
          duration={2500}
          delay={2500}
          useNativeDriver
          iterationCount={Infinity}
            />
          <animatable.Text
          style={{
            fontSize: 20,
            color: 'white',
            padding: 10,
            opacity: fadeText
          }}
          >
            QuizApp
          </animatable.Text>
        </Animated.View>
    </Animated.View>
    );
}

const styles = S
