import React, { useState, useEffect, useRef } from "react";
import { Animated, Image, StatusBar, StyleSheet, Text, View, Keyboard, Dimensions, TouchableWithoutFeedback, TouchableOpacity, TextInput } from 'react-native';
import * as Animatable from 'react-native-animatable'
import { useSafeAreaInsets } from "react-native-safe-area-context";
import Logo from '../../assets/logoIcon/logo.png';
import AppLoading from 'expo-app-loading';
import { useFonts, Inter_900Black } from '@expo-google-fonts/inter';
import SSPLight from '../../assets/fonts/SourceSansPro/SourceSansProLight.ttf';
import SSPRegular from '../../assets/fonts/SourceSansPro/SourceSansProRegular.ttf';
import SSPBold from '../../assets/fonts/SourceSansPro/SourceSansProBold.ttf';
import { Icon } from 'react-native-elements';

const BGColor = "#4D4A95"

export default function InitialLogo() {

  const [loaded] = useFonts({
    SSPLight,
    SSPRegular,
    SSPBold,
  });

  if (!loaded || !BGColor) {
    return 0;
  }

const edges = useSafeAreaInsets();
const upScreen = useRef(new Animated.Value(0)).current;
const fadeLogo = useRef(new Animated.Value(0)).current;
const fadeText = useRef(new Animated.Value(0)).current;

useEffect(() => {

  setTimeout(() => {
    
    Animated.sequence([
      Animated.timing(fadeLogo,
        {
          toValue: 1,
          useNativeDriver: true,
          duration: 1000
        }
      ),
      Animated.timing(fadeText,
        {
          toValue: 1,
          useNativeDriver: true,
          duration: 1000
        }
      ),
      Animated.timing(fadeText,
        {
          toValue: 0,
          delay: 1000,
          useNativeDriver: true,
          duration: 500
        }
        ),
        Animated.timing(upScreen,
          {
            toValue: 100,
            useNativeDriver: true,
            duration: 1000
          }
          ),

    ]).start()

  }, 350)

},[]);

    return(
      <TouchableWithoutFeedback onPress={() => Keyboard.dismiss()}>
      <View style={styles.container}>
        <View style={{ flex: 1, backgroundColor: BGColor, alignItems: 'center', justifyContent: 'center' }}>
        <Animatable.Image 
          source={Logo} 
          style={{
            height: 150, 
            width: 150,
            bottom: 100,
          }}
          animation='rubberBand'
          duration={2500}
          delay={2500}
          useNativeDriver
          iterationCount={Infinity}
            />
        </View>
        <Animatable.Text
          style={styles.titleText}
          animation='fadeInUp'
          delay={1200}
        >
          QuizApp
        </Animatable.Text>
        <View style={styles.bottomView}>
          <Text style={styles.loginText}>Login</Text>
          <View style={styles.inputView}>
            <Icon
              style={styles.inputIcon}
              name='person'
              type='ionicons'
              color='#5352ed'
            />
            <TextInput
              style={styles.input}
              placeholder='Username'
              autoCapitalize='none'
              keyboardType='email-address'
              textContentType='emailAddress'
            />
          </View>
          <View style={styles.inputView}>
            <Icon
              style={styles.inputIcon}
              name='lock'
              type='ionicons'
              color='#5352ed'
            />
            <TextInput
              style={styles.input}
              placeholder='Password'
              secureTextEntry={true}
              autoCapitalize='none'
            />
          </View>
          <Text style={styles.fpText}>Forgot Password?</Text>
          <TouchableOpacity style={styles.loginButton}>
            <Text style={styles.loginButtonText}>Login</Text>
          </TouchableOpacity>
          <Text style={styles.registerText}>
            Don't have an account?
            <Text style={{ color: '#5352ed', fontFamily: 'SSPBold' }}>
              {' Register'}
            </Text>
          </Text>
        </View>
      </View>
    </TouchableWithoutFeedback>
    );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  titleText: {
    position: 'absolute',
    top: Dimensions.get('screen').height * 0.1,
    alignSelf: 'center',
    color: '#fff',
    fontFamily: 'SSPBold',
    fontSize: 60,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 8,
    },
    shadowOpacity: 0.44,
    shadowRadius: 10.32,
    elevation: 16,
  },
  bottomView: {
    backgroundColor: '#fff',
    opacity: 0.95,
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    borderTopLeftRadius: 30,
    borderTopRightRadius: 30,
    paddingTop: 10,
    paddingBottom: 20,
    paddingHorizontal: 20,
  },
  loginText: {
    fontFamily: 'SSPBold',
    fontSize: 24,
    marginTop: 12,
    marginBottom: 4,
  },
  inputView: {
    height: 40,
    borderRadius: 10,
    backgroundColor: '#f1f3f6',
    marginTop: 10,
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
  },
  inputIcon: {
    paddingHorizontal: 8,
  },
  input: {
    height: 40,
    flex: 1,
    fontFamily: 'SSPRegular',
    fontSize: 16,
    color: '#333',
  },
  loginButton: {
    backgroundColor: '#5352ed',
    paddingVertical: 10,
    borderRadius: 8,
    marginTop: 10,
  },
  loginButtonText: {
    color: '#fff',
    fontFamily: 'SSPBold',
    alignSelf: 'center',
    fontSize: 18,
  },
  registerText: {
    alignSelf: 'center',
    marginTop: 12,
    fontFamily: 'SSPRegular',
    fontSize: 16,
  },
  fpText: {
    marginTop: 10,
    alignSelf: 'flex-end',
    fontFamily: 'SSPBold',
    fontSize: 16,
    color: '#5352ed',
  },
  });
